const navbar=document.querySelector('.navbar');window.addEventListener('scroll',()=>{window.scrollY>10?navbar.classList.add('bg-black'):navbar.classList.remove('bg-black')});const questions=document.querySelectorAll('.faq-question');questions.forEach(question=>{question.addEventListener('click',()=>{question.classList.toggle('active');const answer=question.nextElementSibling;answer.classList.toggle('open')})});